            id_description = container + '_ARIAdescription';

            newNode = doc_glob.createElement('div');
            newNode.innerHTML = attr.title;
            newNode.setAttribute('id', id_label);
            newNode.style.display = 'none';
            parent.insertBefore(newNode, node_jsx);

