                            this.createNode('node_op', 'op_mul',
                                this.derivative(node.children[1][1], varname),
                                this.createNode('node_op', 'op_execfun',
                                    this.createNode('node_var', 'log'),
                                    [Type.deepCopy(node.children[1][0])]
                                )
                            )
