z0:1;
z1:%i;
z2:-1;
z3:-%i;
polarz0:polarform(z0);
polarz1:polarform(z1);
polarz2:polarform(z2);
polarz3:polarform(z3);
simp:false;
ta:append([z0],[z1],[z2],[z3]);/*We can use z0, z1, z2, and z3 if we do not want the polar form, but the normal form*/
poly:ev(expand((z-z0)*(z-z1)*(z-z2)*(z-z3)),simp);
