questionvariables.text:
b:rand([3,4,5,6]);
a2:rand([2/(3*b),3/(4*b),4/(5*b),3/(5*b)]);
y1: sin(b*x);           /* curve */
p1: pi/a2;               /* point on curve */
ta6: p1;                  /* x_0 value */
ta7:sin(b*ta6);       /* f(x_0) value */
m_val: b*cos(b*ta6);       /*slope of tangent line */
ta8:decimalplaces(m_val, 2);
yy: sin(b*ta6) - ta6*b*cos(b*ta6);
ta9: decimalplaces(yy, 2) 
ta10: ta8*x + ta9;
ta11: decimalplaces(-ta9/ta8, 2);
