questionvariables.text:
/*Randomisation constants*/
const: rand(4)+2;

/*Question variables*/
cos_extrema: [pi,2*pi,3*pi]; /*equivalent extrema locations for cos(x) - ASCENDING*/
extrema: cos_extrema/const;
x1: 0.1;
x2: ceiling(extrema[3]);
f: cos(const*x);
df: diff(f,x);
d2f: diff(f,x,2);

/*Answers*/
ta1: df;
ta2: extrema;
ta3: d2f;
ta_diff: ev(subst(extrema,x,d2f));
is_min(x):= block([], if(i>0) then return(true) else if(i<0) then return(false));
ta_list: makelist([[null,false,"(not answered)"],[1,is_min(i),"local minimum"],[2,not(is_min(i)),"local maximum"]],i,ta_diff);

/*For display*/
class1: ta_list[1][mcq_correct(ta_list[1])[1]+1][3];
class2: ta_list[2][mcq_correct(ta_list[2])[1]+1][3];
class3: ta_list[3][mcq_correct(ta_list[3])[1]+1][3];

feedbackvariables.text:
sa1: [ans4,ans5,ans6];
sa2: [ans7,ans8,ans9];
sa3: [ans10,ans11,ans12];


indices: [sublist_indices(extrema, lambda([x1], x1 = sa1[1])), sublist_indices(extrema, lambda([x1], x1 = sa2[1])), sublist_indices(extrema, lambda([x1], x1 = sa3[1]))];

points: extrema;
