questionvariables.text:
aa:rand(6)+2;
bb:rand_with_prohib(2, 8, [aa]);
cc:(rand(10)+1)*pi/6;
dd:rand_with_prohib(1,11, [bb*6])*pi/6

taPx:cos(cc)*aa;
taPy:sin(cc)*aa;
taQx:cos(dd)*bb;
taQy:sin(dd)*bb;

feedbackPx:float(cos(bb)*aa);
feedbackPy:float(sin(bb)*aa);
feedbackQx:float(cos(dd)*cc);
feedbackQy:float(sin(dd)*cc);

if (equal(cc*6/pi, 1)) then (feedbackdispangleP:"‎ ") else (feedbackdispangleP:cc*6/pi);
if (equal(dd*6/pi, 1)) then (feedbackdispangleQ:"‎ ") else (feedbackdispangleQ:dd*6/pi);
