questionvariables.text:
rand_x_P:rand_with_prohib(-5, 5, [0]);
rand_y_P:rand_with_prohib(-5, 5, [0]);
rand_x_Q:rand_with_prohib(-5, 5, [0, rand_x_P]);
rand_y_Q:rand_with_prohib(-5, 5, [0, rand_y_P]);

taPr:sqrt(rand_x_P^2 + rand_y_P^2);
taPtheta:atan(rand_y_P/rand_x_P);
taQr:sqrt(rand_x_Q^2 + rand_y_Q^2);
taQtheta:atan(rand_y_Q/rand_x_Q);
