questionvariables.text:
aa: rand(3) + 2;
bb: rand(3) + 3;
ee: rand(3) + 1;

real_roots: [[2,1],[3,1],[4,1],[3,2],[5,2]];
pick : rand(length(real_roots)); /* randomly choose index */
roots : real_roots[pick + 1];
cc : roots[1];
dd : roots[2];
t_exact_1: (1 + sqrt(4*cc - 3))/2;
t_exact_2: (1 - sqrt(4*cc - 3))/2;
t_value_1: float(t_exact_1);
t_value_2: float(t_exact_2);
x_coord: float(t_value_1^3 - cc*t_value_1 - dd);
y_coord: float(t_value_1^2 - t_value_1 - ee);

xexpa: t^2 - aa*t;
yexpa: t^2 - bb*t;
xexpb: t^3 - cc*t - dd;
yexpb: t^2 - t - ee;

dx_dt_1a: diff(xexpa,t);
dy_dt_1a: diff(yexpa,t);
ta1: fullratsimp(dy_dt_1a/dx_dt_1a);
dx_dt_2a: diff(dx_dt_1a, t);
dy_dt_2a: diff(dy_dt_1a, t);
ta2: fullratsimp((dx_dt_1a*dy_dt_2a - dy_dt_1a*dx_dt_2a)/(dx_dt_1a)^3);

dx_dt_1b: diff(xexpb,t);
dy_dt_1b: diff(yexpb,t);
ta3: fullratsimp(dy_dt_1b/dx_dt_1b);
dx_dt_2b: diff(dx_dt_1b, t);
dy_dt_2b: diff(dy_dt_1b, t);
ta4: fullratsimp((dx_dt_1b*dy_dt_2b - dy_dt_1b*dx_dt_2b)/(dx_dt_1b)^3);
