questionvariables.text:
/* Randomisation */
l: rand([0.1, 0.2, 0.5]);
a: rand_with_step(3, 7, 2);
b: float(a / 2);
theta0: rand_with_step(30, 80, 10);

g: a - b * t;
IF: e^(int(l, t));
I: expand(int(IF * g, t)) + C;
gsol: expand(I / IF);
Cexp: rhs(solve(theta = gsol, C)[1]);
C0: float(ev(Cexp, t=0, theta=theta0));
ta: ev(gsol, C=C0);
