questionvariables.text:
/* limits of integration*/
aa: rand_with_step(-4,0,1);
bb: rand_with_prohib(0,4,[aa]);
cc: rand_with_step(-4,0,1);
dd: rand_with_prohib(0,4,[cc]);

/* Bounding box points*/
ymax: bb + 1;
ymin: aa - 1;
xmax: dd + 1;
xmin: cc - 1;

ee: ev((dd)^3/3, simp);
ff: ev((dd)^3/3 + (dd)*y^2 - ((cc)^3/3 + cc*y^2), simp);
gg: ev((cc)^3/3, simp);

exp: x^2 + y^2;
inn: int(exp, x, cc, dd);
ta1: int(inn, y, aa, bb);

/*For worked solution*/
out_int: fullratsimp(int(inn,y));
out_int1: subst(y= bb, out_int);
out_int2: subst(y= aa, out_int);
