questionvariables.text:
aa: 1 + rand(6);
bb: 1 + rand(6);
cc: 1 + rand(6);
dd: 1 + rand(6);
ee: 1 + rand(6);




xexp: aa*u+bb*v-w;
yexp: v-dd*w;
zexp: u+ee*w;

dxdu:diff(xexp,u);
dxdv:diff(xexp,v);
dxdw:diff(xexp,w);


dydu:diff(yexp,u);
dydv:diff(yexp,v);
dydw:diff(yexp,w);


dzdu:diff(zexp,u);
dzdv:diff(zexp,v);
dzdw:diff(zexp,w);

M: matrix ([dxdu, dxdv, dxdw], [dydu, dydv, dydw], [dzdu, dzdv, dzdw]);


ta1: determinant(M);
