questionvariables.text:
aa:rand([2,3]);
yu: rand([3,4,5]);


ta1al:0; ta1au:1; ta1bl:0; ta1bu:2*pi; ta1cl:0; ta1cu:yu;


f1: aa*(1-w/5)*tau*cos(theta);
f2: (1-w/5)*tau*sin(theta);
f3: w;

dxdt: diff(f1,tau);
dxdu: diff(f1,theta);
dxdw: diff(f1,w);

dydt: diff(f2,tau);
dydu: diff(f2,theta);
dydw: diff(f2,w);

dzdt: diff(f3,tau);
dzdu: diff(f3,theta);
dzdw: diff(f3,w);


J: matrix([dxdt,dxdu,dxdw],[dydt,dydu,dydw],[dzdt,dzdu,dzdw]);
JAC: determinant (J);
JAC:trigsimp(JAC);
JAC:factor(JAC);
ta2: JAC;

ta3al:0; ta3au:1; ta3bl:0; ta3bu:2*pi; ta3cl:0; ta3cu:yu;

ta3exp: aa*(1-w/5)^4*tau^3*(4*cos(theta)^2+sin(theta)^2);

fbI11:integrate (ta3exp,w);
I11:integrate (ta3exp,w,ta1cl,ta1cu);
fbI12: integrate (I11,theta);
I12: integrate (I11,theta,ta1bl,ta1bu);
fbI13: integrate (I12,tau);
I13: integrate (I12,tau,ta1al,ta1au);

ta4: I13;
