questionvariables.text:
ta1: r;
aa: rand(3) + 1;
bb: rand(2) + 1;
cc: rand([1/2, 3/2]);
dd: rand(2) + 1;

exp_cart: z*(x^2 + y^2)^cc;
exp_cyl: z*r^(2*cc);
exp_cyl_J: z*r^(2*cc + 1);
zlim_cart: aa + bb*(x^2 + y^2);
zlim_cyl: aa + bb*r^2;

inn1: simplify(int(exp_cyl_J, z, 0, zlim_cyl));
I1: int(exp_cyl_J, z);
inn2: simplify(int(inn1, r, 0, dd));
I2: simplify(int(inn1, r));
ta2: int(inn2, theta, 0, 2*pi);
