questionvariables.text:
aa: rand(5)+2;
bb: rand(2)+2;

if (equal(aa, 2)) then (exp1:sconcat(stack_disp(disp_parens(x),""), "^2")) else (exp1:sconcat(stack_disp(aa/2*(disp_parens(x)),""), "^2"));
if (equal(aa, 4)) then (exp2:y^2) else (exp2:aa/4*y^2);
if (equal(aa, 4)) then (exp3:sconcat(stack_disp(disp_parens(y),""), "^2")) else (exp3:sconcat(stack_disp(aa/4*disp_parens(y),""), "^2"));

