questionvariables.text:
aa:rand(3)+1;
bb:rand(3)+1;


expression:x^aa*z^bb*sin(y);
diffi:diff(expression, x);
diffj:diff(expression, y);
diffk:diff(expression, z);
expres2:subst(z, x, diffi-diffk);
First_int_indef:integrate(expres2, y);
First_int_def:integrate(expres2, y, 0, pi);
Second_int_indef:integrate(First_int_def, z);
Second_int_def:integrate(First_int_def, z, 0, 1);

ta:Second_int_def;
