questionvariables.text:
tx:rand_with_prohib(-3,3,[1,-1]); /* |tx|=1 gives C(0,0) which is to be avoided */
ty:0; /* don't mess with the roots */
cx:rand_with_prohib(-1,1,[0]);
cy:rand_with_prohib(-2,2,[0]);
T(x,y):=[cx*(x+tx),cy*(y+ty)];
TF(y):=expand(ev(T(0,y)[2],x=x/cx-tx)); /* given an expression y=[fn], return a transformed y=[fn2] in new coords */

/* base off the problem with y=-(x-1)(x+1) and y=-4x+5 */
A:T(-1,0); /* quad root */
B:T(1,0); /* quad root */
D:T(2,-3); /* point of tangency */
quad:TF(-(x-1)*(x+1));
ta1:quad;
C:[0,ev(quad,x=0)];
line:TF(-4*x+5);
k_val:coeff(line,x,0);
ta2:k_val;
l:coeff(line,x)*x+k;

/*bounding box*/
bbTL:T(-2,4);
bbBR:T(2,-4);
BBx:[bbTL[1],bbBR[1],-1.5,1.5];
BBy:[bbTL[2],bbBR[2],-1.5,2,C[2]-cy*2]; /*including C[2] here to make sure the y-intercept is visible */
BB:[lmin(BBx),lmax(BBy),lmax(BBx),lmin(BBy)];

/* for the worked solution */
da:coeff(quad-l,x,2);
db:coeff(quad-l,x,1);
dc:coeff(quad-l,x,0);
