questionvariables.text:
qform:k*(x-a)^2*(x-b)*(x-c);
qform_distractors:[y=k*(x+a)^2*(x+b)*(x+c), y=k*(x-a)*(x-b)*(x-c), y=k*(x+abs(a))^2*(x+abs(b))*(x+abs(c)), y=k*(x-a)*(x-b)*(x-c)*(x-y0), y=k*(x+abs(a))*(x+abs(b))*(x+abs(c))*(x+abs(y0))];
a1:rand_with_prohib(-5,5,[0]);
b1:rand_with_prohib(-5,5,[0,a1,a1+1,a1-1,-a1]);
c1:rand_with_prohib(-5,5,[0,a1,a1+1,a1-1,b1,b1+1,b1-1,-a1,-b1]);
y0:rand_with_prohib(-8,8,[a1,b1,c1,-a1,-b1,-c1]); /* make sure the quartic q-intercept has a value distinguishable from the roots */
keqn:ev(qform,[a=a1,b=b1,c=c1,x=0]);
k1:ev(k,solve(keqn=y0,k));
q:ev(qform,[a=a1,b=b1,c=c1,k=k1]);
qform_withabc:ev(qform,[a=a1,b=b1,c=c1]);

mcq:multiselqndisplay([y=ev(qform,[a=a1,b=b1,c=c1])], 1, ev(qform_distractors,[a=a1,b=b1,c=c1]), 5)
ta1:mcq[1];
version:mcq[2];
corr1:mcq_correct(ta1);
incorr1:mcq_incorrect(ta1);

ta2:k1;

/* for the plot - find the location of the TPs to set a sensible view area */
df:diff(q,x);
df0:solve(df=0,x); 
tps:map(lambda([ex],ev([ev(x,ex),ev(q,ex)],float)),df0);
/*bounding box*/
BBx:append(1.4*map(first,tps),[-6,6]); /* use location of TPs (with some extra padding given by the 1.4 factor) as well as default range */
BBy:append(1.4*map(second,tps),[0.5,-0.5,y0+1,y0-1]);
BBy:append(BBy,[-lmax(BBy)/6,-lmin(BBy)/6]) /* ensures the x-axis labels are always visible */
BB:[lmin(BBx),lmax(BBy),lmax(BBx),lmin(BBy)];
