questionvariables.text:
qform:k*(x-a)^3*(x-b);
a1:rand_with_prohib(-5,5,[0]);
b1:rand_with_prohib(-5,5,[0,a1,a1+1,a1-1,-a1,a1+2,a1-2]); /* if the roots are too close together it can be hard to see the behaviour */
y0:rand_with_prohib(-8,8,[0,a1,b1,-a1,-b1]); /* make sure the quartic q-intercept has a value distinguishable from the roots */
keqn:ev(qform,[a=a1,b=b1,x=0]);
k1:ev(k,solve(keqn=y0,k));
q:ev(qform,[a=a1,b=b1,k=k1]);
qform_withabc:ev(qform,[a=a1,b=b1]);

ta1:q;

/* for the plot - find the location of the TPs to set a sensible view area */
df:diff(q,x);
df0:solve(df=0,x); 
tps:map(lambda([ex],ev([ev(x,ex),ev(q,ex)],float)),df0);
/*bounding box*/
BBx:append(1.2*map(first,tps),[-6,6]); /* use location of TPs (with some extra padding given by the 1.2 factor) as well as default range */
BBy:append(1.2*map(second,tps),[1,-1,y0+1,y0-1,-abs(y0)/4]);
BBy:append(BBy,[-abs(lmax(BBy))/6]) /* ensures the x-axis labels are always visible */
BB:[lmin(BBx),lmax(BBy),lmax(BBx),lmin(BBy)];
/* note - the PRT is used to prevent the y-range being too big */
