questionvariables.text:
r1:rand_range(-3,-2);
r2:rand_range(-1,1);
r3:rand_range(2,3);
r4:rand_with_prohib(-4,4,[r1,r2,r3,-r1,-r2,-r3]);
c1:rand_range(1,3);
forms:[(x-r1)*(x-r2)*(x-r3)*(x-r4), (x-r1)^2*(x-r2)*(x-r3), (x-r2)^3*(x-r3), (x-r3)^4, (x-r1)*(x-r2)*(x^2+x+c1), (x+3)*(x+1.5)*(x-1.7)*(x-2.7)/10+2];
funcs:zip_with("*",forms,[1/6,1/5,3/2,1,1/2,1]); /* these multiplicative factors make the plots have nice y-ranges */
theexpressionhas:["four distinct linear factors, giving four distinct real roots", sconcat("the factor \\(",stack_disp((x-r1)^2,""),"\\) giving a repeated root at ",r1), sconcat("the factor \\(",stack_disp((x-r2)^3,""),"\\) giving a flat crossing at ",r2), sconcat("the factor \\(",stack_disp((x-r3)^4,""),"\\) giving a flat turning point at ",r3), "two distinct linear factors which give two real roots, and a quadratic factor with negative discriminant", "two quadratic factors with negative discriminant, so no real roots"];
qform:rand_range(1,5);
q:forms[qform];
/* permute the graphs but keep note of which ones are right! */
reordering:random_permutation([1,2,3,4,5,6]);
fntoletter:zip_with("[",reordering,[A,B,C,D,E,F]);
/* apply the reordering to produce the shuffled list of options */
funcstoplot: makelist(funcs[reordering[i]],i,1,6);
corr:maplist(lambda([ex],assoc(ex,fntoletter)), [1,2,3,4,5,6]);
ta1:maplist(lambda([ex],[ex, is(ex=assoc(qform,fntoletter))]), [A,B,C,D,E,F]);
corr1:corr[qform];
f(x):=q;
ys:append([-4,4],map (lambda ([e], subst (e, f(x))),solve(diff(f(x),x),x)));
thumbnailplot:lambda([ex1,ex2],plot(ex1,[x,-5,5],[y,-5,5],[box, false],[yx_ratio, 1],[xtics, -4, 8, 4],[ytics, -100, 200, 100],[axes, solid],[size,200,200],[plottags,false],[alt,ex2],[color,"#003399"]));
thumbnailplotold:lambda([ex1,ex2],plot(ex1,[x,-5,5],[y,-5,5],[box, false],[yx_ratio, 1],[xtics, -4, 8, 4],[ytics, -6, 12, 6],[axes, solid],[size,200,200],[plottags,false],[alt,ex2]));
