questionvariables.text:
d1: 8;
d2: 12;

f1: 10+ rand([10,20,30,40]);
f2: 20;
f3: 100+ rand([10,20,30,40]);

g1: 24+ rand([4,8,12,16,20]);
g2: 8;
g3: 64+ rand([8,16,24,32,40]);

h1: f1/f2;
h2: f3/f2;

i1: g1/g2;
i2: g3/g2;

opt: d1*x +d2*y;

c1: f1*x+ f2*y <= f3
c2: g1*x+g2*y <= g3;
c3: x >= 0;
c4: y>=0;

eqn1: y = -h1*x + h2;
eqn2: y = -i1*x + i2;
eqn3: x = 0;
eqn4: y = 0;

aa1:solve([eqn1,eqn2],[x,y])[1][1];
aa2:solve([eqn1,eqn2],[x,y])[1][2];

bb1:solve([eqn1,eqn3],[x,y])[1][1];
bb2:solve([eqn1,eqn3],[x,y])[1][2];

cc1:solve([eqn1,eqn4],[x,y])[1][1];
cc2:solve([eqn1,eqn4],[x,y])[1][2];

dd1:solve([eqn2,eqn3],[x,y])[1][1];
dd2:solve([eqn2,eqn3],[x,y])[1][2];

ee1:solve([eqn2,eqn4],[x,y])[1][1];
ee2:solve([eqn2,eqn4],[x,y])[1][2];

aa: ev(opt, aa1,aa2);
bb: ev(opt, bb1,bb2);
cc: ev(opt, cc1,cc2);
dd: ev(opt, dd1,dd2);
ee: ev(opt, ee1,ee2);

ll: [aa,bb,ee];
ss: sort (ll, 'orderlessp)

ta1: ss[3];
