    /**
     * Input formula from flick or balloon on fullkeyboard
     */
    var inputFormula = function (inputText) {
        if (inputText.indexOf("sqrt") == -1) {
            var algArray = inputText.split("");
            $.each(algArray, function (i, alg) {
                var algChar = alg.charCodeAt(0);
                var flickevent = document.createEvent("Event");
                flickevent.initEvent("keypress", true, true, window, 1);
                flickevent.keyCode = algChar;
                flickevent.charCode = algChar;
                flickevent.myCode = algChar;
                //org.mathdox.formulaeditor.FormulaEditor.onkeypress(flickevent);
                document.dispatchEvent(flickevent);
            });
        } else {
            var alg = inputText.split("{");
            alg = alg[1].split("}");
            alg = alg[0];
            var editor = org.mathdox.formulaeditor.FormulaEditor.getFocusedEditor();
            var position = editor.cursor.position;
            var Parser = org.mathdox.formulaeditor.parsing.openmath.OpenMathParser;
            var semantics = new Parser().parse(org.mathdox.formulaeditor.Palettes.defaultPalette);
            var presentation = org.mathdox.formulaeditor.presentation;
            var row = semantics.operands[0].operands[0].operands[6];
            var rowPresentation = new presentation.Row(row.getPresentation(editor.getPresentationContext()));
            rowPresentation.flatten();

            var moveright;
            if (rowPresentation.children) {
                for (var i = 0; i < rowPresentation.children.length; i++) {
                    //alert("inserting: "+i+" : "+toInsert.children[i]);
                    moveright = position.row.insert(position.index,
                        rowPresentation.children[i], (i === 0));
                    if (moveright) {
                        position.index++;
                    }
                }
            } else {
                moveright = position.row.insert(
                    position.index, rowPresentation, true);

                if (moveright) {
                    position.index++;
                }
            }
            editor.redraw();
            editor.save();
            var algChar = 37;
            var flickevent = document.createEvent("Event");
            flickevent.initEvent("keydown", true, true, window, 1);
            flickevent.keyCode = algChar;
            flickevent.charCode = algChar;
            flickevent.myCode = algChar;
            //org.mathdox.formulaeditor.FormulaEditor.onkeypress(flickevent);
            document.dispatchEvent(flickevent);
            var algChar = alg.charCodeAt(0);
            var flickevent = document.createEvent("Event");
            flickevent.initEvent("keypress", true, true, window, 1);
            flickevent.keyCode = algChar;
            flickevent.charCode = algChar;
            flickevent.myCode = algChar;
            //org.mathdox.formulaeditor.FormulaEditor.onkeypress(flickevent);
            document.dispatchEvent(flickevent);
            return true;
        }
        balloonText = "";
    };

